                .list > .wrapper > a > .addr {
                    color:black;
                }
                .list > .wrapper > a > .hope {
                    color:#505050;
                    font-size:12px;
                }
               
                .list > .wrapper > a > img {
                    cursor: pointer;  
                    // transition: all 0.7s; 
                    width: 100%;
                    height: 70%;
                    margin-bottom: 10px;
                }
                
                
