import "../static/less/common.less"
import "../static/css/font2.css"
import { Input, Icon, Menu } from 'antd';

const { SubMenu } = Menu;

class Header extends React.Component {
    state = {
      current: 'loader',
    };


    render() {
        return (

    <div className="header">
        <div className="logo"><img src="../static/images/logo.png" /></div>
        <div className="search">
            <Input className="font2" 
            suffix={<Icon type="search" style={{ color: '#2279E0', position:"absolute", right:"60px", top:"3px"}} />} 
            placeholder="搜索你喜欢的地方" />
        </div>
        <div className="banner">
        <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
        <Menu.Item key="home">
          官网首页
        </Menu.Item>
        <Menu.Item key="loader">
          精选路线
        </Menu.Item>
        <Menu.Item key="live">
            旅游住宿
        </Menu.Item>
        <Menu.Item key="personal">
            专属定制
        </Menu.Item>
      </Menu>
        </div>
      
    </div>
        )}}
  
  export default Header
