import React from 'react'
import "../static/css/font.css"
import $ from 'jquery'

class interest extends React.Component{
    constructor(){
        super()
        this.state = {
            list:[]
        }
    }

    render(){
        return (
            <div className="desrvea">
                <div className="title">
                    <p className="font1">可能感兴趣的地方</p>
                </div>
                <div className="list">
                    <div ref="wrapper" className="wrapper">
                        {this.state.list.map((item,index)=>
                            <a key={index} href={item.src}>
                                <img src={item.pic}/>
                                <p className="addr">{item.addr}</p>
                                <p className="hope">{item.hope}</p>
                            </a>
                        )}
                    </div>
                </div>
            <style jsx>
                {`
                .desrvea {
                    width: 850px;
                    margin: 25px auto;
                }
                .title > p{                   
                    font-size:22px;
                    font-weight:bolder;
                }

                .list {
                    width: 100%;
                    height: 230px;
                    overflow: hidden;
                }

                .list > .wrapper {
                    width:200%;
                    height: 100%;
                }

                .list > .wrapper > a {
                    box-sizing: border-box;
                    display: inline-block;
                    overflow: hidden; 
                    width: 12.5%;
                    height: 100%;
                    padding:5px;                                                                    
                }
                .list > .wrapper > a > p {
                    margin-bottom: 10px;
                    text-align: center; 
                                     
                }
                .list > .wrapper > a > .addr {
                    color:black;
                }
                .list > .wrapper > a > .hope {
                    color:#505050;
                    font-size:12px;
                }
               
                .list > .wrapper > a > img {
                    cursor: pointer;  
                    // transition: all 0.7s; 
                    width: 100%;
                    height: 70%;
                    margin-bottom: 10px;
                }
                
                
                `}
            </style>
            </div>
        )
    }

    componentDidMount(){
        this.getData();
        this.autoCarousel();
    }
    componentWillUnmount(){
        clearInterval(this.carouselTimer);
    }

    async getData(){
        
        let resJson = [
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/146d9b48-477e-4775-9cb7-5b6f895b47c4.jpg',
                'addr':'魅力时尚之旅',
                'hope':'9878人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/b70389b1-4700-4f62-97f8-bbffeac63569.jpg',
                'addr':'非遗文化探秘之旅',
                'hope':'2456人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/23da1f18-a046-422f-9130-c6cfe6e970f1.jpg',
                'addr':'万国建筑世遗之旅',
                'hope':'1235人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/a248cc6e-580a-423e-b3ab-391c2f00218c.jpg',
                'addr':'辉煌金砖路',
                'hope':'7520人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/95f29aed-c300-48ce-896e-11089f1ab0c1.jpg',
                'addr':'醉美西海域',
                'hope':'25236人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/4bfb6a7c-6d80-4ef0-901b-ee70521044b6.jpg',
                'addr':'情迷鼓浪屿',
                'hope':'45620人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/d7a01857-8d8c-4fe1-a771-4bcf5ca43ebe.jpg',
                'addr':'寻百年遗迹',
                'hope':'14250人想去'
            },
            {
                'src':'http://www.example.com/',
                'pic':'http://xm.visitxm.com/tpLineImagesUpload/116c0f53-669c-49ab-b637-05c406068973.jpg',
                'addr':'赏经典华屋',
                'hope':'22356人想去'
            },
        ]

        this.setState({list:resJson});
    }

    //轮播向左移动一格
    carouselToLeft(){
        let $wrapper = $(this.refs.wrapper);
        let $first = $wrapper.children().first().clone();
        $wrapper.animate({marginLeft:'-25%'},3000,'linear',()=>{
            $wrapper.append($first);
            $wrapper.children().first().remove();
            $wrapper.css({marginLeft:'0'});
        });
    }
    //轮播向右一格
    carouselToRight(){
        
    }
    //自动轮播
    autoCarousel(){
        this.carouselTimer = setInterval(()=>{
            this.carouselToLeft();
        },3000);
    }
}
  
  export default interest
